import React from 'react'
import './CardInput.css'

function CardInput({ title, setTitle, desc, setDesc, img, setImg }) {

  const handleClick = (e) => {
    e.target.select();
  };
 
  return (
    <>
    <h1 className="title-above-card">New Title</h1>
      <form className="card">
        <input
          type="text"
          value={title}
          className="title-in-card-input"
          onClick={handleClick}
          onChange={(e) => {
            setTitle(e.target.value);
          }}
        />
        <input
          type="text"
          value={desc}
          className="description-input"
          onClick={handleClick}
          onChange={(e) => {
            setDesc(e.target.value);
          }}
        />
        <label htmlFor="img-upload" className="main-image-area">
          <p className="image-upload-sign">+</p>
          <p className="image-upload-text">IMAGE</p>
        </label>
        <input
          type="file"
          onChange={(e) => {
            // console.log(e.target.files[0]);
            setImageUpload(e.target.files[0]);
            // onImageChange(e.target.files[0]);
            // buttonActivate();
          }}
          accept="image/*"
          id="img-upload"
        />
        <img src={img} id="latest-uploaded-image" alt="" />
        <div className="button-align">
          <button className="submit-button" type="button" 
          onClick={(e)=> {
            // uploadDescText(); 
            // uploadTitleText(); 
            // uploadImage();
            // displayOnCard();
            // resetMainCard();
          }}
          ></button>
        </div>
      </form>
      </>
  )
}

export default CardInput
