// import Axios from "axios";
import "./App.css";
import { useState } from "react";
import CardInput from "./components/cardInput/CardInput";
import CardView from "./components/cardView/CardView";
import { storage } from "./firebase";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { v4 } from "uuid";

function App() {
  const [title, setTitle] = useState("New title");
  const [desc, setDesc] = useState("New description");
  const [img, setImg] = useState(null);
  const [buttonActive, setButtonActive] = useState(false);
  const handleClick = (e) => {
    e.target.select();
  };

  const onImageSelection = (e) => {
    const imageFile = e.target.files[0];
    if (imageFile && imageFile.type.startsWith("image/")) {
      setImg(imageFile);
      setButtonActive(true);
    } else {
      setImg(null);
      alert("Please select a valid image file.");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const uploadedImgUrl = handleImageUpload();
    console.log(uploadedImgUrl);
  };

  const handleImageUpload = () => {
    if (img == null) {
      alert("An Error Occured during upload. Please try again.");
      return;
    } else {
      const imageRef = ref(storage, `images/${img.name + v4()}`);
      const uploadTask = uploadBytesResumable(imageRef, img);

      uploadTask.on('state_changed', (snapshot) => {
        switch (snapshot.state) {
          case 'paused':
            console.log('Upload is paused');
            break;
          case 'running':
            console.log('Upload is running');
            break;
        }
      }, 
      (error) => {
        switch (error.code) {
          case 'storage/unauthorized':
            // User doesn't have permission to access the object
            break;
          case 'storage/canceled':

            break;
          case 'storage/unknown':
            console.log('Unknown error occurred, inspect error.serverResponse'); 
            break;
          default:
            console.log('No Error Occured');
        }
      }, () => {
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          const imgDownloadURL = downloadURL;
          console.log(imgDownloadURL);
        });
      });
    }
  };

  return (
    <div className="page">
      <CardInput
        handleClick={handleClick}
        title={title}
        setTitle={setTitle}
        desc={desc}
        setDesc={setDesc}
        img={img}
        setImg={setImg}
        onImageSelection={onImageSelection}
        buttonActive={buttonActive}
        handleSubmit={handleSubmit}
      />
      <CardView />
    </div>
  );
}

export default App;
