import React from 'react'

const handleClick = (e) => {
  e.target.select();
};

function CardInput() {
  return (
    <>
    <h1 className="title-above-card">New Title</h1>
      <form className="card">
        <input
          type="text"
          defaultValue="New Title"
          className="title-in-card-input"
          onClick={handleClick}
          onChange={(event) => {
            setTitleTextUpload(event.target.value);
          }}
        />
        <input
          type="text"
          defaultValue="New description"
          className="description-input"
          onClick={handleClick}
          onChange={(event) => {
            setDescTextUpload(event.target.value);
          }}
        />
        <label htmlFor="img-upload" className="main-image-area">
          <p className="image-upload-sign">+</p>
          <p className="image-upload-text">IMAGE</p>
          {/* {imageList.map((url) => {
        return <img className="main-image-area" src={url} />
      })} */}
        </label>
        <input
          type="file"
          onChange={(event) => {
            console.log(event.target.files[0]);
            setImageUpload(event.target.files[0]);
            onImageChange(event.target.files[0]);
            buttonActivate();
          }}
          accept="image/*"
          id="img-upload"
        />
        <img src={img} id="latest-uploaded-image" alt="" />
        <div className="button-align">
          <button className="submit-button" type="button" 
          onClick={(e)=> {uploadDescText(); 
            uploadTitleText(); 
            uploadImage();
            displayOnCard();
            resetMainCard();
          }}
          ></button>
        </div>
      </form>
      </>
  )
}

export default CardInput
