import Axios from "axios";
import "./App.css";
import { useState } from "react";
import CardInput from "./components/cardInput/CardInput";
import CardView from "./components/cardView/CardView";
import { storage } from "./firebase";
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { v4 } from "uuid";

function App() {
  const [title, setTitle] = useState("New title");
  const [desc, setDesc] = useState("New description");
  const [img, setImg] = useState(null);
  const [buttonActive, setButtonActive] = useState(false);
  const [imgDownloadURL, setImgDownloadURL] = useState("");
  const [cardToPost, setCardToPost] =useState({});
  const [uploadedCard, setUploadedCard] = useState({});


  const handleClick = (e) => {
    e.target.select();
  };

  const getUploadedCard = () => {
    Axios.get("http://localhost:8080/card").then((response) => {
      console.log(response.data);
      setUploadedCard(response.data);
    });
  };

  const postCard = () => {
    setCardToPost({
      title : title,
      description : desc,

    })
  }

  const onImageSelection = (e) => {
    const imageFile = e.target.files[0];
    if (imageFile && imageFile.type.startsWith("image/")) {
      setImg(imageFile);
      setButtonActive(true);
    } else {
      setImg(null);
      alert("Please select a valid image file.");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleImageUpload();
    postCard();
    getUploadedCard();
  };

  const handleImageUpload = () => {
    if (img == null) {
      alert("An Error Occured during upload. Please try again.");
      return;
    } else {
      const imageRef = ref(storage, `images/${img.name + v4()}`);
      const uploadTask = uploadBytesResumable(imageRef, img);

      uploadTask.on("state_changed", null, null, () => {
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          setImgDownloadURL(downloadURL);
          console.log(imgDownloadURL);
        });
      });
    }
  };

  return (
    <div className="page">
      <CardInput
        handleClick={handleClick}
        title={title}
        setTitle={setTitle}
        desc={desc}
        setDesc={setDesc}
        img={img}
        setImg={setImg}
        onImageSelection={onImageSelection}
        buttonActive={buttonActive}
        handleSubmit={handleSubmit}
      />
      <CardView 
      imgDownloadURL={imgDownloadURL} 
      uploadedCard={uploadedCard} />
    </div>
  );
}

export default App;
